mapper.insert:
insert into QuestionCautionFile(
	FileID
	ParentID,
	FilePath
)
values(
	#{fileID},
	#{parentID},
	#{filePath}
)

mapper.update:
UPDATE QuestionCautionFile set
	FilePath = #{filePath}
WHERE ParentID = #{parentID} AND FileID = #{fileID}

DELETE
FROM QuestionCautionFile
WHERE ParentID = #{parentID} AND FileID = #{fileID}
